template:
* {
      padding: 0px;
      margin: 0;
      box-sizing: border-box;
      background: #456FA0;
}

h1 {
      padding: 10px;
      text-align: center;
}

html,
body,
p {
      font-family: Arial, Helvetica, sans-serif;
      font-size: 1vw;
      margin: 0px;
      padding-left: 0px;
      color: white;
}

.wrapper {
      display: grid;
}

.heading {
      width: 100%;
      display: grid;
      grid-template-columns: 25% 50% 25%;
      grid-gap: 0em;
      height: auto;
      padding: 10px;
}

.left {
      text-align: left;
}

.middle {
      text-align: center;
}

.right {
      text-align: right;
}

.clock {
        font-size: 2vw;
        padding-top:10px;
        font-weight: bold;
}

.nested {
      display: grid;
      justify-content: space-evenly;
      align-content: center;
      grid-template-columns: repeat(6, 1.5fr);
      font-size: 1.5vw;
      text-align: center;
      color: black;
}

.nestedLog {
      display: grid;
      justify-content: space-evenly;
      align-content: center;
      grid-template-columns: repeat(12, 1fr);
      font-size: 1vw;
      text-align: center;
      color: black;
}

.nested10 {
      display: grid;
      justify-content: space-evenly;
      align-content: center;
      grid-template-columns: repeat(10, 1fr);
      font-size: 1vw;
      text-align: center;
      color: black;
}

.wrapper>div {
      background: #eee;
      padding: 0.5em;
}

.wrapper>div:nth-child(odd) {
      background: #ddd;
}

.nested>div {
      padding: 0.5em;
      background-color: inherit;
}

.nestedLog>div {
      padding: 0.5em;
      background-color: inherit;
}

.nested10>div {
      padding: 0.5em;
      background-color: inherit;
}

.bold {
      font-weight: bold;    
}

.flash {
      color: red;
}

.red {
      color: red;
}

    a:link {
      text-decoration: none;
      color: white;
    }

    a:visited {
      text-decoration: none;
      color: white;
    }

    a:hover {
      color: red;
    }

#notes {
